import "./Expression.css"
const Expression = () => {
    const title = "Expression2";
    const array = ['item1','item2', 'item3'];
    const hello = (arg) => `${arg} Function`;
    return (
        <div className="title.toLowerCase()">
       <h3>Hello {title}</h3>
       <h3>{array}</h3>
       <h3>{hello('hello')}</h3>
       <h3>{/* コメントアウトは、画面上に表示されません。 */}</h3>
    </div>
    )
}
export default Expression;
